  public static int floor(String paren){
    int count = 0;
    for (int i = 0; i < paren.length(); i++){
      if (paren.charAt(i) == '('){
        count ++;
      }
      if (paren.charAt(i) == ')'){
        count --;
      }
      if (count == -1){
        System.out.println(i + 1); // code for getting first position to basement
      }
    }
    return count;
  }
